css:
@media screen {
	.print-preview-wrapper, 
	.print-format-gutter {
		background-color: #ddd;
		padding: 15px 0px;
	}
	.print-preview {
		padding: 0px;
		max-width: 8.3in;
		margin: auto;
		min-height: 11.69in;
		position: relative;
	}
	.print-format {
		background-color: white;
		box-shadow: 0px 0px 9px rgba(0,0,0,0.5);
		max-width: 8.3in;
		min-height: 11.69in;
		padding: 0;
		border-radius: 5px;
		margin: auto;
		position: relative;
	}
	#header-html {
		background: #f2f5f5;
		padding: 0.75in 0.75in 0.25in;
	}
	.letter-head {
			float: left;
			width: 50%;
	}
	.print-heading {
		border-bottom: 0;
		margin-bottom: 0;
		float: right;
		width: 50%;
		text-align: right;
	}
	#footer-html {
		position: absolute;
		bottom: 30px;
		left: 0.75in;
		border-top: 1px solid #f5f5f5;
		width: calc(100% - 1.5in);
		padding-top: 25px;
		margin-top: 20px;
	}
	[document-status] {
		margin-bottom: 5mm;
		clear: both;
		display: block;
	}
	[data-fieldtype="Code"] {
		padding: 0 0.75in !important;
	}
	.row.section-break {
		margin: 0;
	}
	.row.section-break .row {
		margin: 0 0 10px;
		border-bottom: 1px solid #e6e7ec;
		padding-bottom: 10px;
	}
	.row.section-break .col-xs-12 > .row {
		border-bottom: 0;
		margin-bottom: 0;
		padding-bottom: 0;
	}

	[data-fieldname="total"] {
		margin-top: 10px;
	}
	.row.section-break .col-xs-6, 
	.row.section-break .col-xs-5,
	.row.section-break .col-xs-7, 
	.row.section-break .col-xs-3,
	.row.section-break .col-xs-9, 
	.row.section-break .col-xs-12 {
		padding: 0;
	}
	#footer-html + .row.section-break,
	#footer-html + .row.section-break +  .row.section-break {
		background: #f2f5f5;
		padding: 0;
		margin: 0;
	}
	#footer-html + .row.section-break .row,
	#footer-html + .row.section-break + .row.section-break .row{
		border: 0;
		padding: 0;
	}
	.row.section-break .col-xs-6 .row:not(:last-child) {
		margin-bottom: 10px;
	}
	.row.section-break .row[data-fieldname="total"] {
		marin-top: 10px;
	}
	.row.section-break .row label{
		margin: 0;
	}
	.print-format .table.table-bordered {
		background: #fff;
		border-top: 0;
		border-left: 0;
		border-right: 0;
		border-bottom: 1px solid #f2f5f5;
		color: #333;
		margin: 0;
	}
	.print-format .table.table-bordered thead > tr > th {
		background: #f2f5f5 !important;
		border: 0;
		border-bottom: 2px solid #f2f5f5;
		padding-top: 20px !important;
		padding-bottom: 20px !important;
	}

	.print-format .row.section-break .col-xs-6:last-child {
		padding-right: 0.75in;
	}
	.print-format .row.section-break .col-xs-6:first-child {
		padding-left: 0.75in;
	}
	.print-format .table.table-bordered thead > tr > th:first-child,
	.print-format .table.table-bordered tbody > tr > td:first-child {
		padding-left: 0.75in !important;
	}

	.print-format .table.table-bordered thead > tr > th:last-child,
	.print-format .table.table-bordered tbody > tr > td:last-child {
		padding-right: 0.75in !important;
	}
	.print-format .table.table-bordered tbody > tr > td {
		border: 1px solid #e6e7ec;
		border-left: 0;
		border-right: 0;
		padding-top: 15px !important;
		padding-bottom: 15px !important;
	}
	.print-format .table.table-bordered tbody > tr:last-child > td {
		border-bottom-width: 2px;
	}
	.section-break .row .value, 
	.section-break .row div {
		font-size: 100%;
		font-weight: normal;
	}
	#footer-html + .section-break .col-xs-6 .data-field {
		border-bottom: 1px solid #c3bdb3;
	}
	#footer-html + .section-break .col-xs-6 .data-field:last-child {
		border-bottom: 0;
	}
	.print-format .row.section-break .col-xs-12 > .row:not(.data-field) .col-xs-6:first-child {
		padding-left: 0.75in;
	}
	.print-format .page-break > .row.section-break {
		margin: 0;
	}
	.print-format .row.section-break .col-xs-6:last-child {
		text-align: right;
	}
}
