css:
@media screen {
	.print-preview-wrapper, 
	.print-format-gutter {
	    padding: 30px 0px;
	    background-color: #f5f7fa;
	    position: relative;
	}
	.print-preview {
	    padding: 0px;
	    max-width: 8.3in;
	    margin: auto;
	    min-height: 11.69in;
	}
	.print-format {
	    font-size: 9pt;
	    font-family: "Helvetica Neue", Helvetica, Arial, "Open Sans", sans-serif;
	    -webkit-print-color-adjust: exact;
	}
	.print-format {
	    background-color: white;
	    box-shadow: 0px 0px 9px rgba(0,0,0,0.5);
	    max-width: 8.3in;
	    min-height: 11.69in;
	    padding: 0;
	    border-radius: 5px;
	    margin: auto;
	    position: relative;
	}
	#header-html {
	    padding: 0.5in;
	    background: #f5fafc;
	    border-radius: 5px 5px 0 0;
	    float: left;
	    width: 100%;
	    margin-bottom: 0.25in;
	}
	.print-format .letter-head, 
	.print-format .print-heading {
	    float: left;
	    width: 50%;
	}
	.print-format .letter-head {
		padding-bottom: 0.25in;
	}
	.print-format .print-heading {
	    float: right;
	    text-align: right;
	    border: 0;
	    margin-bottom: 0;
	    padding-bottom: 0;
	}
	#header-html + div {
		clear: both;
	}
	#header-html .text-center {
		float: left;
			width: 100%;
			border-top: 1px solid #787878;
			padding-top: 10px;
			margin-bottom: 0;
	}	

	div#footer-html {
	    border-radius: 0 0 5px 5px;
	    position: absolute;
	    right: 0;
	    bottom: 0;
	    padding: 0.5in;
	    background: #f9fafc;
	    color: #787878;
	    left: 0;
	}
	.print-format .row.section-break {
	    margin-bottom: 10px;
	    margin-left: 0;
	    margin-right: 0;
	}
	.print-format .row.section-break .row:not(:last-child) {
	    margin-bottom: 10px;
	}
	.print-format .row.section-break .row {
	    margin: 0;
	}
	.print-format .row.section-break .row label {
	    margin: 0;
	}
	.print-format .row.section-break .col-xs-6, 
	.print-format .row.section-break .col-xs-7,
	.print-format .row.section-break .col-xs-3,
	.print-format .row.section-break .col-xs-9,
	.print-format .row.section-break .col-xs-12,
	.print-format .row.section-break .col-xs-5 {
		padding-right: 0;
		padding-left: 0;
	}
	.print-format .row.section-break .value {
	    color: #787878;
	    font-weight: normal;
	    font-size: 100%;
	}
	.print-format .row.section-break .row .col-xs-7,
	.print-format .row.section-break .row .col-xs-3 {
	    color: #787878;
	    font-weight: normal;
	    font-size: 100%;
	}
	.print-format .row .col-xs-6.column-break:first-child {
	    padding-left: 0.5in;
	}
	.print-format .row .col-xs-6:last-child {
	    padding-right: 0.5in;
	    text-align: right;
	}
	.value-color {
		color: #787878 !important;
	}

	[data-fieldtype=Code] {
	    padding: 0 0.5in !important;
	}
	.table-bordered {
	    border-left: 0;
	    border-right: 0;
	    margin-bottom: 0;
	    margin-top: 10px;
	}
	.print-format .table thead tr th:first-child,
	.print-format .table tbody tr td:first-child {
		padding-left: 0.5in !important;
		border-left: 0;
	}
	.print-format .table thead tr th:last-child,
	.print-format .table tbody tr td:last-child {
		border-right: 0;
		padding-right: 0.5in !important;
	}
	.print-format .table thead tr th, 
	.print-format .table tbody tr td {
		padding-top: 10px !important;
		padding-bottom: 10px !important;
		border-left: 0;
			border-right: 0;
	}
	.print-format .table tbody tr td {
		color: #787878 !important;		
	}
	.print-format-gutter #header-html {
	    padding-bottom: 0;
	}

	.print-format-gutter #header-html + .header-wrap {
		padding-top: 0;
		min-height: 140px;
	}		
	.print-format-gutter .header-wrap {
		background: #f5fafc;
		padding: 0.25in 0;
		min-height: 160px;
	}
	.print-format-gutter .print-format hr {
		display: none;
	}
}

.print-format.landscape {
	max-width: 11.69in;
	padding: 0.2in;
}

.page-break {
	padding: 30px 0px;
	border-bottom: 1px dashed #888;
}

.page-break:first-child {
	padding-top: 0px;
}

.page-break:last-child {
	border-bottom: 0px;
}

/* mozilla hack for images in table */
body:last-child .print-format td img {
	width: 100% !important;
}

@media print {
	.print-format p {
		margin-left: 1px;
		margin-right: 1px;
	}
}

.square-image {
	width: 100%;
	height: 0;
	padding: 50% 0;
	background-size: contain;
	/*background-size: cover;*/
	background-repeat: no-repeat !important;
	background-position: center center;
	border-radius: 4px;
}

.print-item-image {
	object-fit: contain;
}

.pdf-variables,
.pdf-variable,
.visible-pdf {
	display: none !important;
}

.print-heading h4 {
	margin-top: 5px;
}

table.no-border, table.no-border td {
	border: 0px;
}

.print-format label {
	/* wkhtmltopdf breaks label into multiple lines when it is inline-block */
	display: block;
}

.print-format img {
	max-width: 100%;
}

.print-format table td > .primary:first-child {
	font-weight: bold;
}

.print-format td, .print-format th {
	vertical-align: top !important;
	padding: 6px !important;
}

.print-format p {
	margin: 3px 0px 3px;
}

table td div {
	/* needed to avoid partial cutting of text between page break in wkhtmltopdf */
	page-break-inside: avoid !important;
}

/* hack for webkit specific browser */
@media (-webkit-min-device-pixel-ratio:0) {
	thead, tfoot { display: table-row-group; }
}
