css:
@media screen {

	.print-format-gutter {
		background-color: #ddd;
		padding: 15px 0px;
	}
	.print-format {
		background-color: #fff8ef;
		border: 10px solid #2a6fd5;
		max-width: 8.3in;
		min-height: 11.69in;
		padding: 0.75in;
		margin: auto;
		font-family: "-apple-system", "BlinkMacSystemFont", "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
		position: relative;
	}
	.print-heading {
		border-bottom: 0;
		padding-bottom: 0;
		float: right;
		width: 50%;
		text-align: right;
	}

	#header-html {
		float: left;
		width: 100%
	}
	.letter-head {
		float: left;
		width: 50%;
	}
	#footer-html {
	    position: absolute;
	    bottom: 20px;
	    right: 0.75in;
	    left: 0.75in;
	    padding-top: 0.25in;
	    border-top: 1px solid #c3bdb3;
	}

	.row.section-break .col-xs-6 {
		width: calc(50% - 10px);
	}
	.row.section-break .col-xs-6 .row {
	    border-bottom: 1px solid #2a6fd5;
	    padding-bottom: 10px;
		margin-bottom: 10px;
		color: #2a6fd5;
	}
	.row.section-break .col-xs-6:first-child {
		margin-right: 10px;
	}

	.row.section-break .col-xs-6:last-child {
		margin-left: 10px;
		text-align: right;
	}

	#footer-html + .row.section-break, 
	#footer-html + .row.section-break + .row.section-break {
		color: #36414c;
	}

	#footer-html + .row.section-break .row, 
	#footer-html + .row.section-break + .row.section-break .row{
		border-bottom-color: #36414c;
		color: #36414c;
	}
	#footer-html + .row.section-break + .row.section-break .row:last-child 		{
		border-bottom: 0;
	}

	#footer-html + .row.section-break .row .col-xs-5, 
	#footer-html + .row.section-break .row .col-xs-7, 
	#footer-html + .row.section-break .row .col-xs-3, 
	#footer-html + .row.section-break .row .col-xs-9, 
	#footer-html + .row.section-break + .row.section-break .row .col-xs-5, 
	#footer-html + .row.section-break + .row.section-break .row .col-xs-7, 
	#footer-html + .row.section-break + .row.section-break .row .col-xs-3, 
	#footer-html + .row.section-break + .row.section-break .row .col-xs-9 {
		display: block;
		width: 100%;
		padding-top: 5px;
	}
	[data-fieldtype="Table"] {
	    margin: 0 -15px;
	}

	.table.table-bordered {
		border-top: 2px solid #2a6fd5;
		border-left: 0;
		border-right: 0;
		border-bottom: 1px solid #2a6fd5;
		color: #5393e0;
		margin-bottom: 0;
	}
	.table.table-bordered thead > tr > th {
		background: none !important;
		border: 2px solid #2a6fd5;
		border-left: 0;
		border-right: 0;
		text-transform: uppercase;
		color: #2a6fd5;
	}
	.table.table-bordered tbody > tr > td {
		border: 1px solid #2a6fd5;
		border-left: 0;
		border-right: 0;
		color: #2a6fd5;
	}
	.table.table-bordered tbody > tr:last-child > td {
	    border-bottom-width: 2px;
	}
	.table.table-bordered tbody > tr > td:first-child,
	.table.table-bordered thead > tr > th:first-child {
		padding-left: 15px  !important;
	}
	.table.table-bordered tbody > tr > td:last-child,
	.table.table-bordered thead > tr > th:last-child {
		padding-right: 15px  !important;
	}
	.important .value {
		font-weight: normal;
		font-size: 100%;
	}
	document-status] {
   		margin-bottom: 5mm;
    		clear: both;
    		display: block;
	}
}
